package Interface;import javax.swing.*;import java.awt.event.MouseAdapter;import java.awt.event.MouseEvent;public class ClickEffect extends MouseAdapter {    private Menu menu;    public ClickEffect(Menu menu1){        menu = menu1;    }    public void mouseEntered(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/Play_Button2.png"));            menu.getLabelButtonPlay().setIcon(iconPlay);        }    }    public void mouseExited(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/Play_Button.png"));            menu.getLabelButtonPlay().setIcon(iconPlay);        }    }    public void mousePressed(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            System.out.println("clicked");            menu.getCardLayout().show(menu.getMenu(),"GAME_PANEL");            menu.getGamePanel().startGame();        }    }}